mapper.delete:
DELETE
FROM
  user_addr
WHERE
  id = #{userAddr.id}
AND userId = #{userAddr.userId}

mapper.select:
SELECT account.id, info.username AS userName, account.name, info.phone, info.bank_card AS bankCard, account.createTime FROM user_info info,user_account account WHERE 1=1
AND account.name = #{userInfo.name}
AND info.phone = #{userInfo.phone}
AND DATE_FORMAT(account.createTime,'%Y-%m-%d') >= #{userInfo.createStartTime}
AND DATE_FORMAT(account.createTime,'%Y-%m-%d') <= #{userInfo.createEndTime}
